    private static readonly byte[,] s_lookupTable;

    static HeaderConverter()
    {
        s_lookupTable = new byte[HeaderTypeMax, 256];
        for (int i = 0; i < HeaderTypeMax; i++)
        {
            for (int j = 0; j < 256; j++)
            {
                int index = s_convertTable[256 * i + j];
                s_lookupTable[i, index] = (byte)j;
            }
        }
    }

    public static byte DecodeHeader(byte value, int headerType)
    {
        if (headerType < 0 || headerType >= HeaderTypeMax)
        {
            throw new ArgumentException("header type out of range");
        }

        return s_lookupTable[headerType, value];
    }
